import React, { Component } from 'react'
import { Consumer } from '../context'
import axios from "axios"

export default class Addtodo extends Component {
    state={
        id:4,
        title:"",
        complete:false
    }
    update=(e)=>{
        this.setState({
            title: e.target.value
        })
    }
    add=(dispatch,e)=>{
        e.preventDefault()
        const newTodo = this.state
        axios.post("/todos", newTodo)
        .then(res=> dispatch({ type:'ADD',payload: res.data }))
        this.setState({title:""})
    }
    render() {
        return (
        <Consumer>{value=>{
            const { dispatch } = value
            return <form onSubmit={this.add.bind(this,dispatch)}>
            <label onChange={this.update} value={this.state.title}>
            <input type="text" className='form-control rounded-0' placeholder='write your task'  />
            <input type="text"  />
            </label>
            <button className='form-control rounded-0 btn-secondary' type='submit'>Add Todo</button>
        </form>
        }}

        </Consumer>
        
        )
    }
}
