import React, { Component } from 'react'
import './style.css'
import { Consumer  } from '../context'
import axios from 'axios';

export default class Todo extends Component {
    style=()=>{
        const { complete } = this.props.todo
        return { textDecoration: complete? "line-through " : "none"}
    }
    toggle=(id, dispatch)=>{
        dispatch({type:"TOGGLE", payload: id})
    }
    remove=(id, dispatch)=>{
        axios.delete(`/todos/${id}`)
        .then(() => dispatch({ type: "REMOVE" , payload: id}))
        
    }
    render() {
        const { title,_id } = this.props.todo
        const current = new Date();
        const date = `${current.getDate()}/${current.getMonth()+1}/${current.getFullYear()}`;

        return (
            <Consumer>{value=>{
                    const {dispatch} = value
                    const [favorite,setFavorite]=useState(false)
                    const [stats,setStats]=useState(false)
                    return <h3 className='text-dark text-center p-1 bg-light border-bottom' style={this.style()}>
                    <i   className='far fa-times-circle fa-sm float-left m-1 text-danger fl' onClick={this.remove.bind(this,_id,dispatch)} ></i>{title}
                    <input type="checkbox" className='m-2 float-right fr' onChange={this.toggle.bind(this,
                    _id,dispatch)} />
                    <p className='fr'>{date}</p>
                    <div className={classes.fotter_button}>
                <button onClick={()=>{setFavorite(true)}} style={{backgroundColor:favorite==true?"orange":""}}>
                  <FontAwesomeIcon
                    icon={faStar}
                    aria-hidden="true"
                  />
                </button>
                <button onClick={()=>{setStats(true)}} style={{backgroundColor:stats==true?"blue":""}}>
                  <FontAwesomeIcon
                    icon={faChartBar}
                    aria-hidden="true"
                  />
                </button>
              </div>
            </h3>
            }
        }
            </Consumer>
        )
    }
}
