import React, { Component } from 'react'
import { Consumer } from '../context'
import axios from "axios"
import Popup from './Popup';

export default class Addtodo extends Component {
    state={
        id:4,
        title:"",
        complete:false
    }
    update=(e)=>{
        this.setState({
            title: e.target.value
        })
    }
    add=(dispatch,e)=>{
        e.preventDefault()
        const newTodo = this.state
        axios.post("/todos", newTodo)
        .then(res=> dispatch({ type:'ADD',payload: res.data }))
        this.setState({title:""})
    }
    App=()=> {
        const [isOpen, setIsOpen] = useState(false);
       
        const togglePopup = () => {
          setIsOpen(!isOpen);
        }}
    render() {
        return (
        <Consumer>{value=>{
            const { dispatch } = value
            return <form onSubmit={this.add.bind(this,dispatch)}>
            <input type="text" className='form-control rounded-0' placeholder='write your task' onChange={this.update} value={this.state.title} />
            
            <button className='form-control rounded-0 btn-secondary' type='submit'>Add Todo</button>
            <input
      type="button"
      value="Click to Open Popup"
      onClick={togglePopup}
    />
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
    {isOpen && <Popup
      content={<>
        <b>Design your Popup</b>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
        <button>Test button</button>
      </>}
      handleClose={togglePopup}
    />}
        </form>
        }}

        </Consumer>
        
        )
    }
}
