import React, { Component } from 'react'
import { Consumer } from '../context'
import axios from "axios"

export default class Addtodo extends Component {
    state={
        id:4,
        title:"",
        complete:false
    }
    update=(e)=>{
        this.setState({
            title: e.target.value
        })
    }
    add=(dispatch,e)=>{
        e.preventDefault()
        const newTodo = this.state
        axios.post("/todos", newTodo)
        .then(res=> dispatch({ type:'ADD',payload: res.data }))
        this.setState({title:""})
    }
    createFruit = (e)=> {
        e.preventDefault();
        //get the fruit object name from the form
        var fruit = this.refs.fruitName.value;
        //if we have a value
        //call the addFruit method of the App component
        //to change the state of the fruit list by adding an new item
        if(typeof fruit === 'string' && fruit.length > 0) {
        this.props.addFruit(fruit);
        //reset the form
        this.refs.fruitForm.reset();
        }}
    render() {
        return (
        <Consumer>{value=>{
            const { dispatch } = value
            return <form onSubmit={this.add.bind(this,dispatch)}>
            <input type="text" className='form-control rounded-0' placeholder='write your task' onChange={this.update} value={this.state.title} />
            <div className="form-group">
            <label for="fruitItem">
              Fruit Name
              <input type="text" id="fruitItem" placeholder="e.x.lemmon" ref="fruitName" className="form-control" />
            </label>
            </div>
            <button type="submit" className="btn btn-primary">Add Fruit</button>
            <button className='form-control rounded-0 btn-secondary' type='submit'>Add Todo</button>
        </form>
        }}

        </Consumer>
        
        )
    }
}
